  componentWillReceiveProps(nextProps){

    if(!nextProps.loading){
      this.setState({
        'userDetails':nextProps.user,
      });
    }

  }

	 render(){

    if(!this.props.loading){
      return(            
        <div>
          <div className="content-wrapper">
            <section className="content">
