                                  Name <span className="pull-right">:</span>
                                  </div>  
                                  <div className="col-lg-8 col-md-8 col-sm-8 col-xs-8 text-left userValue">
                                    <p>{this.state.userDetails.profile.firstname} {this.state.userDetails.profile.lastname}</p>
