                                                                                React.createElement(
                                                                                    "span",
                                                                                    {
                                                                                        className: "input-group-addon",
                                                                                        id: "basic-addon1"
                                                                                    },
                                                                                    React.createElement("i", {
                                                                                        className: "fa fa-circle-o-notch",
                                                                                        "aria-hidden": "true"
                                                                                    })
                                                                                ),
                                                                                React.createElement(
                                                                                    "select",
                                                                                    {
                                                                                        className: "form-control newProductStatus",
                                                                                        "aria-describedby": "basic-addon1",
                                                                                        ref: "statusRef"
                                                                                    },
                                                                                    React.createElement(
                                                                                        "option",
                                                                                        null,
                                                                                        "Draft"
                                                                                    ),
                                                                                    React.createElement(
                                                                                        "option",
                                                                                        null,
                                                                                        "Publish"
                                                                                    ),
                                                                                    React.createElement(
                                                                                        "option",
                                                                                        null,
                                                                                        "Unpublish"
                                                                                    )
                                                                                )
                                                                            )
                                                                        )
                                                                    )
                                                                ),
                                                                React.createElement(
                                                                    "div",
                                                                    {
                                                                        className: "col-lg-12 col-md-12 col-sm-12 col-xs-12 add-new-productCol"
                                                                    },
                                                                    React.createElement(
                                                                        "div",
                                                                        {
                                                                            className: "add-new-product-submit"
                                                                        },
                                                                        React.createElement("input", {
                                                                            type: "submit",
                                                                            className: "btn btn-primary",
                                                                            value: this.state.productMode == "EditMode" ? "Update Product" : "Add Product"
                                                                        })
                                                                    )
                                                                )
                                                            )
                                                        )
                                                    )
                                                )
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            );
        }

        return render;
    }();

    return AddNewProduct;
}(TrackerReact(Component)); //
